mapper.sql:

			and t_user.id = #{query.userId}


			and t_user.id  >=  #{query.userIdMin}


			and t_user.id  <=  #{query.userIdMax}


			and t_user.id  >=  #{query.userIdMin} and t_user.id  <=  #{query.userIdMax}


			and t_user.phone like concat('%', #{query.userPhone}, '%')


			and t_user.email like concat('%', #{query.userEmail}, '%')


			and t_user.create_time = #{query.userCreateTime}


			and t_user.create_time  >=  #{query.userCreateTimeMin}


			and t_user.create_time  <=  #{query.userCreateTimeMax}


			and t_user.create_time  >=  #{query.userCreateTimeMin} and t_user.create_time  <=  #{query.userCreateTimeMax}


			and t_user_detail.nickname like concat('%', #{query.userDetailNickname}, '%')


			and t_user_detail.headicon like concat('%', #{query.userDetailHeadicon}, '%')


			and t_user_detail.identity like concat('%', #{query.userDetailIdentity}, '%')


			and t_user_detail.real_name like concat('%', #{query.userDetailRealName}, '%')


			and t_user_detail.gender = #{query.userDetailGender}


			and t_user_detail.gender  >=  #{query.userDetailGenderMin}


			and t_user_detail.gender  <=  #{query.userDetailGenderMax}


			and t_user_detail.gender  >=  #{query.userDetailGenderMin} and t_user_detail.gender  <=  #{query.userDetailGenderMax}


			and t_user_detail.birthday = #{query.userDetailBirthday}


			and t_user_detail.birthday  >=  #{query.userDetailBirthdayMin}


			and t_user_detail.birthday  <=  #{query.userDetailBirthdayMax}


			and t_user_detail.birthday  >=  #{query.userDetailBirthdayMin} and t_user_detail.birthday  <=  #{query.userDetailBirthdayMax}


			and t_user_detail.age = #{query.userDetailAge}


			and t_user_detail.age  >=  #{query.userDetailAgeMin}


			and t_user_detail.age  <=  #{query.userDetailAgeMax}


			and t_user_detail.age  >=  #{query.userDetailAgeMin} and t_user_detail.age  <=  #{query.userDetailAgeMax}


			and t_user_detail.qq like concat('%', #{query.userDetailQq}, '%')


			and t_user_detail.qq_qrcode like concat('%', #{query.userDetailQqQrcode}, '%')


			and t_user_detail.wechat like concat('%', #{query.userDetailWechat}, '%')


			and t_user_detail.wechat_qrcode like concat('%', #{query.userDetailWechatQrcode}, '%')


			and t_user_detail.alipay like concat('%', #{query.userDetailAlipay}, '%')


			and t_user_detail.alipay_qrcode like concat('%', #{query.userDetailAlipayQrcode}, '%')


			and t_user_detail.share_code like concat('%', #{query.userDetailShareCode}, '%')

		
        and
        t_user.id = t_user_detail.id

mapper.select:
select

from t_user, t_user_detail

    

order by

    ${query.sortColumn} ${query.sortOrder}


    t_user.update_time desc, t_user.create_time desc

limit #{query.beginIndex}, #{query.pageSize}
