mapper.sql:

			and t_user.id = #{query.userId}


			and t_user.id  >=  #{query.userIdMin}


			and t_user.id  <=  #{query.userIdMax}


			and t_user.id  >=  #{query.userIdMin} and t_user.id  <=  #{query.userIdMax}


			and t_user.phone like concat('%', #{query.userPhone}, '%')


			and t_user.email like concat('%', #{query.userEmail}, '%')


			and t_user_social.id = #{query.userSocialId}


			and t_user_social.id  >=  #{query.userSocialIdMin}


			and t_user_social.id  <=  #{query.userSocialIdMax}


			and t_user_social.id  >=  #{query.userSocialIdMin} and t_user_social.id  <=  #{query.userSocialIdMax}


			and t_user_social.openid like concat('%', #{query.userSocialOpenid}, '%')


			and t_user_social.union_id like concat('%', #{query.userSocialUnionId}, '%')


			and t_user_social.access_token like concat('%', #{query.userSocialAccessToken}, '%')


			and t_user_social.session_key like concat('%', #{query.userSocialSessionKey}, '%')


			and t_user_social.refresh_token like concat('%', #{query.userSocialRefreshToken}, '%')


			and t_user_social.social_type like concat('%', #{query.userSocialSocialType}, '%')


			and t_user_social.version = #{query.userSocialVersion}


			and t_user_social.version  >=  #{query.userSocialVersionMin}


			and t_user_social.version  <=  #{query.userSocialVersionMax}


			and t_user_social.version  >=  #{query.userSocialVersionMin} and t_user_social.version  <=  #{query.userSocialVersionMax}


			and t_user_social.create_time = #{query.userSocialCreateTime}


			and t_user_social.create_time  >=  #{query.userSocialCreateTimeMin}


			and t_user_social.create_time  <=  #{query.userSocialCreateTimeMax}


			and t_user_social.create_time  >=  #{query.userSocialCreateTimeMin} and t_user_social.create_time  <=  #{query.userSocialCreateTimeMax}


			and t_user_social.update_time = #{query.userSocialUpdateTime}


			and t_user_social.update_time  >=  #{query.userSocialUpdateTimeMin}


			and t_user_social.update_time  <=  #{query.userSocialUpdateTimeMax}


			and t_user_social.update_time  >=  #{query.userSocialUpdateTimeMin} and t_user_social.update_time  <=  #{query.userSocialUpdateTimeMax}

		
        and
        t_user.id = t_user_social.user_id

mapper.select:
select

from t_user, t_user_social

    

order by

    ${query.sortColumn} ${query.sortOrder}


    t_user.update_time desc, t_user.create_time desc

limit #{query.beginIndex}, #{query.pageSize}
